ruleToc.chapterList:
@js:
function spArr(arr, num){
	let newArr = []
	for(let i = 0; i < arr.length;){
		newArr.push(arr.slice(i, i += num))
	}
	return newArr
}
let res = JSON.parse(result).data
let item_list = spArr(res["item_list"], 100)
let array = []
for(let i = 0; i < item_list.length; i ++){
	let response = java.ajax('https://novel.snssdk.com/api/novel/book/directory/detail/v1/?item_ids='+item_list[i])
	let data = JSON.parse(response).data
	data.forEach((x) => {
		array.push({
			name: x.title,
			url: '/api/novel/book/reader/full/v1/?aid=2329&item_id='+x.item_id,
			info: ((Number(x.volume_name)==0?"第一卷：默认":x.volume_name)+' | '+java.timeFormat(x.first_pass_time*1000)+' | '+x.chapter_word_number+'字')
		})
	})
}
array

ruleSearch.bookList:
.c_row
<js>
result.toArray().slice(0, 3).map(el => {
	var url = String(el.select('a:eq(1)').attr('href')).replace(/^\//, baseUrl.match(/(.+?\/\/[^\/]+\/)/)[1]);
	var J = org.jsoup.Jsoup.parse(java.ajax(url));
	var o = selector => String(J.select(selector).text());
	var og = selector => String(J.select('[property="og:' + selector + '"]').attr('content'));
	return {
		name: og('novel:book_name').replace(/(全文|小说|免费阅读|最新章节).*|[(（].*[）)]/g, ''),
		author: og('novel:author'),
		kind: og('novel:category') + ',' + og('novel:status').replace(/中|已/, ''),
		intro: og('description').replace(/.*(观看小说|简介)[:：]|各位书友.*/g, '').replace(/\s+/g, ''),
		cover: og('image'),
		url: url,
	};
}); 
</js>

