        tags: d.tags,
        url: `https://novel.snssdk.com/api/novel/book/directory/list/v1?book_id=${d.book_id}`
      });
    }
  }
  list

bookSourceComment:
let getFileSize = (size)=> {
        if (size <= 0) {
            return "";
        }
        let unitForm = ["Byte", "KB", "MB", "GB", "TB"];
        for (let i = 0, len = unitForm.length; i < len; i++) {
            if (size > 1024) {
                size /= 1024;
                continue;
            } else {
                return size.toFixed(2).replace(/(\.00)$/, "") + unitForm[i];
            }
        }
        return "ERROR:数值过大";
 }

