ruleContent.content:
@js:
intro=java.getString('chapterIntro||data.chapterintro');
intro=intro!=''?'▍'+intro+'\n':'';
content=java.getString('content||message');
coin=java.getString('porint');
coin=coin!=''?'\n'+coin+'币（请在晋江APP内购买）':'';
say=java.getString('sayBody');
say=say!=''?'\n▍作话：\n'+say:'';
if(baseUrl.match(/token/)){
vipc=/^DcAWGQtI44o=$/.test(content)?JSON.parse(src).message:java.createSymmetricCrypto('DES/CBC/PKCS5Padding', String('KK!%G3JdCHJxpAF3%Vg9pN'), String('1ae2c94b')).decryptStr(content)
result=intro+vipc+coin+say
}else{result=intro+content+say}
if(!baseUrl.match(/undefined/)){
nid=baseUrl.match(/novelId=(\d+)/)[1];
cid=baseUrl.match(/chapterId=(\d+)/)[1];
url='https://static.jjwxc.net/comment_json.php?novelid='+nid+'&chapterid='+cid;
list=JSON.parse(java.ajax(url)).body;
comment='';
lim=book.getVariable("custom");
lim=lim!=''?lim:5;
len=list.length>lim?lim:list.length;
for(i=0;i<len;i++){
comment+='•&nbsp;'+list[i].ip_pos+'－'+list[i].commentauthor+'\n'+list[i].commentbody+'\n'
}
comment='\n▍章评：\n'+comment;
result=result+comment
}

